import{_ as e,c as t,d as i,a as r,o as n,r as o}from"./app.a14a8f87.js";const b=JSON.parse('{"title":"\u6E05\u6670\u6613\u61C2\u7684\u73B0\u4EE3\u7F16\u7A0B\u8BED\u8A00\u5185\u5B58\u7BA1\u7406","description":"","frontmatter":{},"headers":[],"relativePath":"WEB/\u8FDB\u51FB\u7684V8/01-\u6E05\u6670\u6613\u61C2\u7684\u73B0\u4EE3\u7F16\u7A0B\u8BED\u8A00\u5185\u5B58\u7BA1\u7406.md","lastUpdated":1668008455000}'),s={name:"WEB/\u8FDB\u51FB\u7684V8/01-\u6E05\u6670\u6613\u61C2\u7684\u73B0\u4EE3\u7F16\u7A0B\u8BED\u8A00\u5185\u5B58\u7BA1\u7406.md"},l=r('<h1 id="\u6E05\u6670\u6613\u61C2\u7684\u73B0\u4EE3\u7F16\u7A0B\u8BED\u8A00\u5185\u5B58\u7BA1\u7406" tabindex="-1">\u6E05\u6670\u6613\u61C2\u7684\u73B0\u4EE3\u7F16\u7A0B\u8BED\u8A00\u5185\u5B58\u7BA1\u7406 <a class="header-anchor" href="#\u6E05\u6670\u6613\u61C2\u7684\u73B0\u4EE3\u7F16\u7A0B\u8BED\u8A00\u5185\u5B58\u7BA1\u7406" aria-hidden="true">#</a></h1><h2 id="\u76EE\u5F55" tabindex="-1">\u76EE\u5F55 <a class="header-anchor" href="#\u76EE\u5F55" aria-hidden="true">#</a></h2><nav class="table-of-contents"><ul><li><a href="#\u76EE\u5F55">\u76EE\u5F55</a></li><li><a href="#\u5185\u5B58\u7BA1\u7406\u662F\u4EC0\u4E48">\u5185\u5B58\u7BA1\u7406\u662F\u4EC0\u4E48</a></li><li><a href="#\u6808stack">\u6808Stack</a></li><li><a href="#\u5806heap">\u5806Heap</a></li><li><a href="#\u4E3A\u4EC0\u4E48\u5185\u5B58\u7BA1\u7406\u5F88\u91CD\u8981">\u4E3A\u4EC0\u4E48\u5185\u5B58\u7BA1\u7406\u5F88\u91CD\u8981</a></li><li><a href="#\u4E0D\u540C\u7684\u5B9E\u73B0\u65B9\u5F0F\uFF1F">\u4E0D\u540C\u7684\u5B9E\u73B0\u65B9\u5F0F\uFF1F</a><ul><li><a href="#\u624B\u52A8\u5185\u5B58\u7BA1\u7406">\u624B\u52A8\u5185\u5B58\u7BA1\u7406</a></li><li><a href="#\u5783\u573E\u6536\u96C6-gc">\u5783\u573E\u6536\u96C6(GC)</a></li><li><a href="#\u8D44\u6E90\u83B7\u53D6\u5373\u521D\u59CB\u5316-raii">\u8D44\u6E90\u83B7\u53D6\u5373\u521D\u59CB\u5316(RAII)</a></li><li><a href="#\u81EA\u52A8\u5F15\u7528\u8BA1\u6570-arc">\u81EA\u52A8\u5F15\u7528\u8BA1\u6570(ARC)</a></li><li><a href="#\u6240\u6709\u6743\uFF08ownership\uFF09">\u6240\u6709\u6743\uFF08Ownership\uFF09</a></li></ul></li><li><a href="#\u53C2\u8003">\u53C2\u8003</a></li></ul></nav><p>\u5728\u8FD9\u4E2A\u7531\u591A\u4E2A\u90E8\u5206\u7EC4\u6210\u7684\u7CFB\u5217\u6587\u7AE0\u4E2D\uFF0C\u6211\u7684\u76EE\u6807\u662F\u8981\u8BB2\u660E\u767D\u5185\u5B58\u7BA1\u7406\u80CC\u540E\u7684\u6982\u5FF5\uFF0C\u5E76\u6DF1\u5165\u89C2\u5BDF\u4E00\u4E9B\u73B0\u4EE3\u7F16\u7A0B\u8BED\u8A00\u7684\u5185\u5B58\u7BA1\u7406\u65B9\u5F0F\u3002\u6211\u5E0C\u671B\u8BE5\u7CFB\u5217\u6587\u7AE0\u80FD\u5E26\u4F60\u4E86\u89E3\u8FD9\u4E9B\u7F16\u7A0B\u8BED\u8A00\u5185\u5B58\u7BA1\u7406\u65B9\u9762\u662F\u5982\u4F55\u8FD0\u4F5C\u7684\u3002\u5B66\u4E60\u5185\u5B58\u7BA1\u7406\u4E5F\u5C06\u4F1A\u6709\u52A9\u4E8E\u6211\u4EEC\u5199\u51FA\u6027\u80FD\u66F4\u597D\u7684\u4EE3\u7801\uFF0C\u56E0\u4E3A\u6211\u4EEC\u5199\u4EE3\u7801\u7684\u65B9\u5F0F\u4E5F\u4F1A\u5F71\u54CD\u5230\u5185\u5B58\u7BA1\u7406\uFF0C\u4E0D\u7BA1\u7F16\u7A0B\u8BED\u8A00\u6709\u6CA1\u6709\u63D0\u4F9B\u81EA\u52A8\u5185\u5B58\u7BA1\u7406\u6280\u672F\uFF08automatic memory management technique\uFF09\u3002</p><p>\u5185\u5B58\u7BA1\u7406\u662F\u63A7\u5236\u548C\u534F\u8C03\u5E94\u7528\u7A0B\u5E8F\u8BBF\u95EE<strong>\u8BA1\u7B97\u673A\u5185\u5B58</strong>\u7684\u65B9\u5F0F\u7684\u8FC7\u7A0B\u3002\u5B83\u5728\u8F6F\u4EF6\u5DE5\u7A0B\u9886\u57DF\u662F\u4E00\u4E2A\u5F88\u91CD\u8981\u7684\u8BDD\u9898\uFF0C\u4E14\u662F\u4E00\u4E2A\u56F0\u6270\u4E86\u5F88\u591A\u4EBA\u7684\u8BDD\u9898\uFF0C\u5BF9\u5F88\u591A\u4EBA\u6765\u8BF4\uFF0C\u5B83\u5C31\u662F\u4E00\u4E2A\u9ED1\u76D2\uFF08black box\uFF09\u3002</p><h2 id="\u5185\u5B58\u7BA1\u7406\u662F\u4EC0\u4E48" tabindex="-1">\u5185\u5B58\u7BA1\u7406\u662F\u4EC0\u4E48 <a class="header-anchor" href="#\u5185\u5B58\u7BA1\u7406\u662F\u4EC0\u4E48" aria-hidden="true">#</a></h2><p>\u5F53\u4E00\u4E2A\u8F6F\u4EF6\u8FD0\u884C\u5728\u4E00\u53F0\u8BA1\u7B97\u673A\u7684\u76EE\u6807\u64CD\u4F5C\u7CFB\u7EDF\u4E0A\u65F6\uFF0C\u5B83\u9700\u8981\u8BBF\u95EE\u8BA1\u7B97\u673A\u7684\u5185\u5B58 <strong>RAM</strong> (Random-access memory) \u6765\uFF1A</p><ul><li>\u52A0\u8F7D\u81EA\u5DF1\u7684\u9700\u8981\u88AB\u6267\u884C\u7684\u5B57\u8282\u7801\uFF08<strong>bytecode</strong>\uFF09\u3002</li><li>\u5B58\u50A8\u88AB\u6267\u884C\u7A0B\u5E8F\u4F7F\u7528\u7684\u6570\u636E\u503C\uFF08<strong>data values</strong>\uFF09\u548C\u6570\u636E\u7ED3\u6784\uFF08<strong>data structure</strong>\uFF09\u3002</li><li>\u52A0\u8F7D\u7A0B\u5E8F\u6267\u884C\u65F6\u6240\u5FC5\u9700\u7684\u4EFB\u4F55\u8FD0\u884C\u65F6\u7CFB\u7EDF\uFF08<strong>run-time systems</strong>\uFF09\u3002</li></ul><p>\u5F53\u4E00\u4E2A\u8F6F\u4EF6\u7A0B\u5E8F\u4F7F\u7528\u5185\u5B58\u65F6\uFF0C\u7528\u5230\u4E86\u4E24\u4E2A\u5185\u5B58\u533A\u57DF\uFF0C\u9664\u4E86\u7528\u4E8E\u52A0\u8F7D\u5B57\u8282\u7801\u7684\u7A7A\u95F4\u4E4B\u5916\uFF0C\u662F\u6808\uFF08<strong>Stack</strong>\uFF09\u548C\uFF08<strong>Heap</strong>\uFF09\u5806\u5185\u5B58\u3002</p><h2 id="\u6808stack" tabindex="-1">\u6808 Stack <a class="header-anchor" href="#\u6808stack" aria-hidden="true">#</a></h2><p>\u6808 stack \u7528\u4E8E <strong>\u9759\u6001\u5185\u5B58\u5206\u914D</strong>\uFF08static memory allocation\uFF09\uFF0C\u5176\u540D\u5B57\u4E5F\u8868\u793A\u4E86\u5B83\u662F\u4E00\u4E2A\u540E\u8FDB\u5148\u51FA\u6A21\u578B <strong>LIFO</strong>\uFF08last in first out\uFF09 (\u53EF\u4EE5\u628A\u5B83\u60F3\u8C61\u4E3A\u4E00\u53E0\u76D2\u5B50)\u3002</p><ul><li>\u7531\u4E8E\u6808\u7684\u7279\u6027\uFF0C\u4ECE\u6808\u4E2D\u5B58\u50A8\u548C\u83B7\u53D6\u6570\u636E\u7684\u8FC7\u7A0B\u662F\u975E\u5E38\u5FEB\u7684\uFF0C\u65E0\u9700\u67E5\u627E\u64CD\u4F5C\u3002\u4F60\u53EA\u9700\u8981\u4ECE\u6808\u7684\u6700\u9876\u5C42\u5B58\u50A8\u548C\u83B7\u53D6\u6570\u636E\u5C31\u597D\u4E86\u3002</li><li>\u4F46\u662F\u8FD9\u4E5F\u610F\u5473\u7740\u5B58\u50A8\u5728\u6808\u4E0A\u7684\u4EFB\u4F55\u6570\u636E\u5FC5\u987B\u662F <strong>\u6709\u9650\u548C\u9759\u6001\u7684\uFF08finite and static\uFF09</strong> (\u6570\u636E\u7684\u5927\u5C0F\u5728\u7F16\u8BD1\u65F6\uFF08compile-time\uFF09\u786E\u5B9A)\u3002</li><li>\u6808\u662F\u51FD\u6570\u7684\u6267\u884C\u6570\u636E\u88AB\u4FDD\u5B58\u7684\u5730\u65B9\uFF0C\u4FDD\u5B58\u7684\u5F62\u5F0F\u4E3A\u6808\u5E27\uFF08<strong>stack frames</strong>\uFF09(\u6808\u5E27\u662F\u51FD\u6570\u7684\u5B9E\u9645\u6267\u884C\u533A\u57DF)\u3002\u6BCF\u4E00\u5E27\u662F\u6808\u4E2D\u7684\u4E00\u5757\u7A7A\u95F4\uFF0C\u51FD\u6570\u6240\u9700\u6570\u636E\u88AB\u5B58\u50A8\u4E8E\u6B64\u3002\u6BD4\u5982\uFF0C\u6BCF\u6B21\u4E00\u4E2A\u51FD\u6570\u58F0\u660E\u4E00\u4E2A\u65B0\u7684\u53D8\u91CF\uFF0C\u8FD9\u4E2A\u53D8\u91CF\u5C31\u88AB push \u5230\u6808\u7684\u6700\u9876\u5C42\u3002\u7136\u540E\u6BCF\u6B21\u4E00\u4E2A\u51FD\u6570\u9000\u51FA\uFF0C\u6808\u9876\u5C31\u4F1A\u88AB\u6E05\u7406\uFF0C\u8FD9\u6837\u6240\u6709\u7684\u88AB\u8BE5\u51FD\u6570 push \u5230\u6808\u9876\u7684\u53D8\u91CF\u90FD\u4F1A\u88AB\u6E05\u9664\u6389\u3002\u7531\u4E8E\u6808\u6570\u636E\u5B58\u50A8\u7684\u9759\u6001\u7279\u6027\uFF0C\u4EE5\u4E0A\u884C\u4E3A\u4F1A\u5728\u7F16\u8BD1\u65F6\u786E\u5B9A\u3002</li><li>\u5728\u591A\u7EBF\u7A0B\u5E94\u7528\u7A0B\u5E8F\uFF08<strong>Multi-threaded applications</strong>\uFF09\u4E2D\uFF0C\u6BCF\u4E2A\u7EBF\u7A0B\u62E5\u6709\u4E00\u4E2A\u6808\uFF08<strong>stack per thread</strong>\uFF09\u3002</li><li>\u6808\u7684\u5185\u5B58\u7BA1\u7406\u65E2\u7B80\u5355\u53C8\u76F4\u63A5\uFF08<strong>simple and straightforward</strong>\uFF09\uFF0C\u7531\u64CD\u4F5C\u7CFB\u7EDF\u6765\u5B8C\u6210\u3002</li><li>\u5B58\u50A8\u5728\u6808\u4E0A\u7684\u5178\u578B\u6570\u636E\u5305\u62EC\u5C40\u90E8\u53D8\u91CF\uFF08<strong>local variables</strong>\uFF09 (\u503C\u7C7B\u578B\u6216\u539F\u59CB\u7C7B\u578B\uFF0C\u539F\u59CB\u5E38\u91CF)\uFF0C\u6307\u9488\uFF08<strong>pointers</strong>\uFF09\u548C\u51FD\u6570\u5E27\uFF08<strong>function frames</strong>\uFF09\u3002</li><li>\u8FD9\u91CC\u662F\u4F60\u4F1A\u9047\u5230\u6808\u6EA2\u51FA\u9519\u8BEF\uFF08<strong>stack overflow errors</strong>\uFF09\u7684\u5730\u65B9\uFF0C\u56E0\u4E3A\u76F8\u8F83\u4E8E\u5806\u6765\u8BF4\uFF0C\u6808\u7684\u5927\u5C0F\u662F\u53D7\u5230\u9650\u5236\u7684\u3002</li><li>\u5927\u90E8\u5206\u7F16\u7A0B\u8BED\u8A00\u5BF9\u80FD\u5B58\u50A8\u5728\u6808\u4E0A\u7684<strong>\u503C\u7684\u5927\u5C0F</strong>\u90FD\u6709\u6240\u9650\u5236\uFF08limit on the size\uFF09\u3002</li></ul><p><img data-zooming src="https://res.craft.do/user/full/eb7c2bf3-27c1-e91b-6644-86577d3bb98f/doc/64848389-C161-4439-AF77-4EBEA7A754D9/909FA0BB-F25B-49AD-9F1D-7041257D3F98_2/PnpBHrfLBrDIc8yxCM17yRyZBC7UztxqcOBGmoL30Ywz/7KpvEn1.gif" alt="7KpvEn1.gif" data-src="https://res.craft.do/user/full/eb7c2bf3-27c1-e91b-6644-86577d3bb98f/doc/64848389-C161-4439-AF77-4EBEA7A754D9/909FA0BB-F25B-49AD-9F1D-7041257D3F98_2/PnpBHrfLBrDIc8yxCM17yRyZBC7UztxqcOBGmoL30Ywz/7KpvEn1.gif"></p><p>\u4E0A\u56FE\u4E3A JavaScript \u4E2D\u6808\u7684\u4F7F\u7528\u60C5\u51B5\uFF0C\u5BF9\u8C61\u88AB\u5B58\u50A8\u5728\u5806\u4E2D\uFF0C\u9700\u8981\u7684\u65F6\u5019\u88AB\u5F15\u7528\u3002\u89C6\u9891\u6F14\u793A\uFF1A</p>',14),c=r('<h2 id="\u5806heap" tabindex="-1">\u5806 Heap <a class="header-anchor" href="#\u5806heap" aria-hidden="true">#</a></h2><p>\u5806\u7528\u4E8E\u52A8\u6001\u5185\u5B58\u5206\u914D\uFF08<strong>dynamic memory allocation</strong>\uFF09\uFF0C\u4E0E\u6808\u4E0D\u540C\uFF0C\u7A0B\u5E8F\u9700\u8981\u7528\u6307\u9488\uFF08<strong>pointers</strong>\uFF09\u5728\u5806\u4E2D\u67E5\u627E\u6570\u636E (\u53EF\u4EE5\u628A\u5B83\u60F3\u8C61\u4E3A\u4E00\u4E2A\u5927\u7684\u591A\u7EA7\u56FE\u4E66\u9986)\u3002</p><ul><li>\u5806\u6BD4\u6808\u66F4\u6162\uFF08<strong>slower</strong>\uFF09\uFF0C\u5806\u6D89\u53CA\u5230\u66F4\u591A\u7684\u67E5\u627E\u6570\u636E\u7684\u8FC7\u7A0B\uFF0C\u4F46\u662F\u5B83\u53EF\u4EE5\u5B58\u50A8\u66F4\u591A\u7684\u6570\u636E\u3002</li><li>\u8FD9\u610F\u5473\u7740\u5806\u53EF\u4EE5\u5B58\u50A8\u52A8\u6001\u5927\u5C0F\uFF08<strong>dynamic size</strong>\uFF09\u7684\u6570\u636E\u3002</li><li>\u5BF9\u4E8E\u6574\u4E2A\u5E94\u7528\u7A0B\u5E8F\u7684\u6240\u6709\u7EBF\u7A0B\uFF0C\u5806\u662F\u5171\u4EAB\u7684\uFF08<strong>shared</strong>\uFF09\u3002</li><li>\u7531\u4E8E\u5806\u7684\u52A8\u6001\u7279\u6027\uFF0C\u4F7F\u5176\u66F4\u96BE\u4E8E\u7BA1\u7406\uFF0C\u5806\u4E5F\u662F\u5927\u90E8\u5206\u5185\u5B58\u7BA1\u7406\u95EE\u9898\u51FA\u73B0\u7684\u5730\u65B9\uFF0C\u540C\u65F6\u4E5F\u662F\u7F16\u7A0B\u8BED\u8A00\u81EA\u52A8\u5185\u5B58\u7BA1\u7406\u89E3\u51B3\u65B9\u6848\u51FA\u73B0\u7684\u539F\u56E0\u3002</li><li>\u5B58\u50A8\u5728\u5806\u4E2D\u7684\u5178\u578B\u6570\u636E\u5305\u62EC\u5168\u5C40\u53D8\u91CF\uFF08<strong>global variables</strong>\uFF09\u3001\u5F15\u7528\u7C7B\u578B\uFF08<strong>reference types</strong>\uFF09\u6BD4\u5982\u5BF9\u8C61\u3001\u5B57\u7B26\u4E32\u3001\u5B57\u5178\uFF0C\u5176\u5B83\u590D\u6742\u7684\u6570\u636E\u7ED3\u6784\u3002</li><li>\u5982\u679C\u4F60\u7684\u5E94\u7528\u7A0B\u5E8F\u5C1D\u8BD5\u4F7F\u7528\u66F4\u591A\u8D85\u51FA\u5806\u73B0\u6709\u53EF\u7528\u7684\u7A7A\u95F4\u65F6\uFF0C\u4F1A\u9047\u5230\u5185\u5B58\u4E0D\u8DB3\u9519\u8BEF\uFF08<strong>out of memory errors</strong>\uFF09\u3002(\u5C3D\u7BA1\u4E5F\u4F1A\u6709\u50CF\u5783\u573E\u6536\u96C6\uFF08GC\uFF09\u3001\u7D27\u7F29\u5904\u7406\uFF08compacting\uFF09\u8FD9\u6837\u8BB8\u591A\u5176\u5B83\u7684\u56E0\u7D20\u7684\u5F71\u54CD)\u3002</li><li>\u901A\u5E38\uFF0C\u5B58\u50A8\u5728\u5806\u4E0A\u7684\u503C\u7684\u5927\u5C0F\u662F\u6CA1\u6709\u9650\u5236\u7684\u3002\u5F53\u7136\uFF0C\u5206\u914D\u7ED9\u5E94\u7528\u7A0B\u5E8F\u7684\u5185\u5B58\u662F\u6709\u4E0A\u9650\u7684\u3002</li></ul><h2 id="\u4E3A\u4EC0\u4E48\u5185\u5B58\u7BA1\u7406\u5F88\u91CD\u8981" tabindex="-1">\u4E3A\u4EC0\u4E48\u5185\u5B58\u7BA1\u7406\u5F88\u91CD\u8981 <a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u5185\u5B58\u7BA1\u7406\u5F88\u91CD\u8981" aria-hidden="true">#</a></h2><p>\u4E0D\u50CF\u786C\u76D8\u5B58\u50A8\uFF0CRAM \u5BB9\u91CF\u662F\u6709\u9650\u7684\u3002\u5982\u679C\u4E00\u4E2A\u7A0B\u5E8F\u4E00\u76F4\u5728\u6D88\u8017\u5185\u5B58\u800C\u4E0D\u53BB\u91CA\u653E\uFF0C\u6700\u7EC8\u5C06\u5BFC\u81F4\u5185\u5B58\u4E0D\u8DB3\uFF0C\u7A0B\u5E8F\u5D29\u6E83\uFF0C\u751A\u81F3\u66F4\u7CDF\u7CD5\u7684\u662F\uFF0C\u64CD\u4F5C\u7CFB\u7EDF\u4E5F\u4F1A\u8DDF\u7740\u5D29\u6E83\u3002\u56E0\u6B64\u8F6F\u4EF6\u7A0B\u5E8F\u4E0D\u80FD\u4E00\u76F4\u968F\u5FC3\u6240\u6B32\u5730\u5360\u7528\u5185\u5B58\uFF0C\u56E0\u4E3A\u8FD9\u5C06\u4F1A\u5BFC\u81F4\u5176\u5B83\u7A0B\u5E8F\u548C\u8FDB\u7A0B\u5185\u5B58\u4E0D\u8DB3\u3002\u56E0\u6B64\u4E3A\u4E86\u4E0D\u8BA9\u8F6F\u4EF6\u5F00\u53D1\u4EBA\u5458\u81EA\u5DF1\u6765\u7BA1\u7406\u5185\u5B58\uFF0C\u5927\u90E8\u5206\u7F16\u7A0B\u8BED\u8A00\u63D0\u4F9B\u4E86\u81EA\u52A8\u7BA1\u7406\u5185\u5B58\u7684\u65B9\u5F0F\u3002\u800C\u4E14\u5F53\u6211\u4EEC\u8C08\u5230\u5185\u5B58\u7BA1\u7406\u65F6\uFF0C\u6211\u4EEC\u4E3B\u8981\u8BA8\u8BBA\u7684\u662F\u5806\u5185\u5B58\u7684\u7BA1\u7406\u3002</p><h2 id="\u4E0D\u540C\u7684\u5B9E\u73B0\u65B9\u5F0F\uFF1F" tabindex="-1">\u4E0D\u540C\u7684\u5B9E\u73B0\u65B9\u5F0F\uFF1F <a class="header-anchor" href="#\u4E0D\u540C\u7684\u5B9E\u73B0\u65B9\u5F0F\uFF1F" aria-hidden="true">#</a></h2><p>\u56E0\u4E3A\u73B0\u4EE3\u7F16\u7A0B\u8BED\u8A00\u4E0D\u60F3\u7ED9\u7EC8\u7AEF\u7684\u5F00\u53D1\u4EBA\u5458\u589E\u52A0\u589E\u52A0\u7BA1\u7406\u5E94\u7528\u7A0B\u5E8F\u5185\u5B58\u7684\u8D1F\u62C5 (\u66F4\u591A\u60C5\u51B5\u4E0B\u662F\u4E0D\u4FE1\u4EFB\u4ED6\u4EEC\u{1F445})\uFF0C\u5927\u90E8\u5206\u7684\u8BED\u8A00\u8BBE\u8BA1\u51FA\u4E86\u4E00\u79CD\u81EA\u52A8\u7BA1\u7406\u5185\u5B58\u7684\u65B9\u5F0F\u3002\u4E00\u4E9B\u8F83\u8001\u7684\u8BED\u8A00\u4ECD\u7136\u9700\u8981\u624B\u52A8\u7BA1\u7406\u5185\u5B58\uFF0C\u4F46\u5176\u4E2D\u4E00\u4E9B\u8BED\u8A00\u4E5F\u63D0\u4F9B\u4E86\u7B80\u6D01\u7684\u5904\u7406\u65B9\u5F0F\u3002\u4E00\u4E9B\u8BED\u8A00\u4F7F\u7528\u4E86\u591A\u79CD\u5185\u5B58\u7BA1\u7406\u7684\u65B9\u5F0F\uFF0C\u4E00\u4E9B\u8BED\u8A00\u751A\u81F3\u8BA9\u5F00\u53D1\u4EBA\u5458\u53BB\u9009\u62E9\u6700\u9002\u5408\u4ED6\u7684\u4E00\u79CD (C++ \u5C31\u662F\u4E00\u4E2A\u597D\u4F8B\u5B50)\u3002\u8FD9\u4E9B\u5185\u5B58\u7BA1\u7406\u65B9\u5F0F\u5206\u4E3A\u5982\u4E0B\u51E0\u7C7B\uFF1A</p><h3 id="\u624B\u52A8\u5185\u5B58\u7BA1\u7406" tabindex="-1">\u624B\u52A8\u5185\u5B58\u7BA1\u7406 <a class="header-anchor" href="#\u624B\u52A8\u5185\u5B58\u7BA1\u7406" aria-hidden="true">#</a></h3><p>\u7F16\u7A0B\u8BED\u8A00\u9ED8\u8BA4\u4E0D\u4F1A\u4E3A\u4F60\u7BA1\u7406\u5185\u5B58\uFF0C\u9700\u8981\u4F60\u81EA\u5DF1\u4E3A\u521B\u5EFA\u7684\u5BF9\u8C61\u5206\u914D\u548C\u91CA\u653E\u5185\u5B58\u3002\u6BD4\u5982\uFF0C<strong>C</strong> \u548C <strong>C++</strong> \uFF0C\u5B83\u4EEC\u63D0\u4F9B\u4E86 <code>malloc</code> \u3001 <code>realloc</code> \u3001 <code>calloc</code> \u548C <code>free</code> \u65B9\u6CD5\u53BB\u7BA1\u7406\u5185\u5B58\uFF0C\u5F00\u53D1\u4EBA\u5458\u5728\u7A0B\u5E8F\u4E2D\u4F7F\u7528\u8FD9\u4E9B\u65B9\u6CD5\u5206\u914D\u548C\u91CA\u653E\u5806\u5185\u5B58\uFF0C\u5229\u7528\u6307\u9488\u6709\u6548\u5730\u7BA1\u7406\u5185\u5B58\u3002\u4F46\u6211\u4EEC\u8BF4\u8FD9\u5E76\u4E0D\u9002\u5408\u6240\u6709\u4EBA\u{1F609}\u3002</p><h3 id="\u5783\u573E\u6536\u96C6-gc" tabindex="-1">\u5783\u573E\u6536\u96C6 (GC) <a class="header-anchor" href="#\u5783\u573E\u6536\u96C6-gc" aria-hidden="true">#</a></h3><p>\u5783\u573E\u6536\u96C6\uFF08garbage collection\uFF09\u662F\u6307\uFF0C\u901A\u8FC7\u91CA\u653E\u4E0D\u518D\u4F7F\u7528\u7684\u5DF2\u5206\u914D\u5185\u5B58\u7684\u5806\u5185\u5B58\u81EA\u52A8\u7BA1\u7406\u65B9\u5F0F\u3002\u5728\u73B0\u4EE3\u7F16\u7A0B\u8BED\u8A00\u4E2D\uFF0CGC \u662F\u6700\u5E38\u89C1\u7684\u5185\u5B58\u7BA1\u7406\u65B9\u5F0F\u4E4B\u4E00\uFF0C\u5783\u573E\u6536\u96C6\u8FC7\u7A0B\u7ECF\u5E38\u8FD0\u884C\u5728\u7279\u5B9A\u7684\u65F6\u95F4\u95F4\u9694\uFF08intervals\uFF09\u5185\uFF0C\u56E0\u6B64\u53EF\u80FD\u4F1A\u4EA7\u751F\u8F7B\u5FAE\u7684\u5F00\u9500\uFF0C\u88AB\u79F0\u4E3A \u201C\u6682\u505C\u65F6\u95F4\u201D\uFF08pause times\uFF09\u3002</p><p><strong>JVM(Java/Scala/Groovy/Kotlin)</strong> , <strong>JavaScript</strong> , <strong>C#</strong> , <strong>Golang</strong> , <strong>OCaml</strong> , \u548C <strong>Ruby</strong> \u8FD9\u4E9B\u7F16\u7A0B\u8BED\u8A00\u9ED8\u8BA4\u4F7F\u7528\u5783\u573E\u6536\u96C6\u7684\u5185\u5B58\u7BA1\u7406\u65B9\u5F0F\u3002</p><p><img data-zooming src="https://res.craft.do/user/full/eb7c2bf3-27c1-e91b-6644-86577d3bb98f/doc/64848389-C161-4439-AF77-4EBEA7A754D9/9297E28E-2D24-4252-97D3-1785739BA9FC_2/pZblri32GtRYwpzLcDOVVC4XZmiTxqGoAywJtf1pC10z/AZaR0LP.gif" alt="AZaR0LP.gif" data-src="https://res.craft.do/user/full/eb7c2bf3-27c1-e91b-6644-86577d3bb98f/doc/64848389-C161-4439-AF77-4EBEA7A754D9/9297E28E-2D24-4252-97D3-1785739BA9FC_2/pZblri32GtRYwpzLcDOVVC4XZmiTxqGoAywJtf1pC10z/AZaR0LP.gif"></p><ul><li><strong>Mark &amp; Sweep GC</strong>\uFF08\u6807\u8BB0\u6E05\u9664 GC\uFF09\uFF1A\u4E5F\u88AB\u79F0\u4E3A <strong>Tracing GC</strong> \u3002\u5B83\u901A\u5E38\u662F\u4E00\u4E2A\u4E24\u6B65\u7B97\u6CD5\uFF0C\u7B2C\u4E00\u6B65\u6807\u8BB0\u90A3\u4E9B\u4ECD\u7136\u88AB\u5F15\u7528\u4E3A \u201Calive\u201D \u72B6\u6001\u7684\u5BF9\u8C61\uFF0C\u4E0B\u4E00\u6B65\u91CA\u653E\u90A3\u4E9B\u975E \u201Calive\u201D \u72B6\u6001\u7684\u5BF9\u8C61\u7684\u5185\u5B58\u3002<strong>JVM</strong> , <strong>C#</strong> , <strong>Ruby</strong> , <strong>JavaScript</strong> , \u548C<strong> Golang</strong> \u6B63\u662F\u4F7F\u7528\u4E86\u8FD9\u79CD\u65B9\u5F0F\u3002JVM \u4E2D\u6709\u7740\u4E0D\u540C\u7684 GC \u7B97\u6CD5\u53EF\u4EE5\u9009\u62E9\uFF0C\u800C\u50CF<strong> V8 \u8FD9\u6837\u7684 JavaScript \u5F15\u64CE\u4F7F\u7528\u4E86 Mark &amp; Sweep GC\uFF0C\u540C\u65F6\u5F15\u7528\u8BA1\u6570 GC \u7528\u4E8E\u8865\u5145</strong>\u3002\u8FD9\u79CD GC \u4E5F\u53EF\u7528\u4E8E C \u548C C++\uFF0C\u4E0D\u8FC7\u662F\u4EE5\u4E00\u4E2A<a href="https://en.wikipedia.org/wiki/Boehm_garbage_collector" target="_blank" rel="noreferrer">\u5916\u90E8\u4F9D\u8D56\u5E93</a>\u7684\u65B9\u5F0F\u63D0\u4F9B\u7684\u3002</li><li><strong>Reference counting GC</strong>\uFF08\u5F15\u7528\u8BA1\u6570 GC\uFF09\uFF1A\u8FD9\u79CD\u6536\u96C6\u65B9\u5F0F\u4E2D\uFF0C\u6BCF\u4E2A\u5BF9\u8C61\u90FD\u4F1A\u83B7\u5F97\u4E00\u4E2A\u5F15\u7528\u8BA1\u6570\uFF08reference count\uFF09\uFF0C\u968F\u7740\u5B83\u88AB\u5F15\u7528\u6B21\u6570\u7684\u53D8\u5316\u800C\u589E\u52A0\u6216\u51CF\u5C11\uFF0C\u5F53\u5F15\u7528\u8BA1\u6570\u53D8\u4E3A 0 \u65F6\uFF0C\u5783\u573E\u6536\u96C6\u8FC7\u7A0B\u4E5F\u5C31\u5B8C\u6210\u4E86\u3002\u56E0\u4E3A\u8FD9\u79CD\u65B9\u5F0F\u4E0D\u80FD\u5904\u7406\u5FAA\u73AF\u5F15\u7528\uFF08cyclic references\uFF09\u7684\u95EE\u9898\uFF0C\u6240\u4EE5\u5E76\u4E0D\u662F\u975E\u5E38\u63A8\u8350\u3002 <strong>PHP</strong> , <strong>Perl</strong> , \u548C <strong>Python</strong> \u5C31\u4F7F\u7528\u4E86\u8FD9\u79CD\u7C7B\u578B\u7684 GC \u65B9\u5F0F\u800C\u4E14\u63D0\u4F9B\u4E86\u89E3\u51B3\u5FAA\u73AF\u5F15\u7528\u7684\u65B9\u6CD5\uFF08workarounds\uFF09\u3002\u8FD9\u79CD\u7C7B\u578B\u7684 GC \u4E5F\u53EF\u4EE5\u7528\u5728 C++ \u4E0A\u3002</li></ul><h3 id="\u8D44\u6E90\u83B7\u53D6\u5373\u521D\u59CB\u5316-raii" tabindex="-1">\u8D44\u6E90\u83B7\u53D6\u5373\u521D\u59CB\u5316 (RAII) <a class="header-anchor" href="#\u8D44\u6E90\u83B7\u53D6\u5373\u521D\u59CB\u5316-raii" aria-hidden="true">#</a></h3><p>RAII\uFF08<strong>Resource Acquisition is Initialization</strong>\uFF09\u3002\u8FD9\u79CD\u7C7B\u578B\u7684\u5185\u5B58\u7BA1\u7406\u4E2D\uFF0C\u4E00\u4E2A\u5BF9\u8C61\u7684\u5185\u5B58\u5206\u914D\u88AB\u7ED1\u5B9A\u5230\u4E86\u5B83\u7684\u751F\u547D\u5468\u671F\u4E0A\uFF0C\u5BF9\u8C61\u521B\u5EFA\u5C31\u5206\u914D\uFF0C\u9500\u6BC1\u5C31\u91CA\u653E\u3002\u5B83\u88AB\u5F15\u5165\u5230\u4E86 C++ \u4E2D\uFF0C\u4E5F\u88AB<strong> Ada</strong> \u548C<strong> Rust</strong> \u4F7F\u7528\u7740\u3002</p><div class="tip custom-block"><p class="custom-block-title">\u8D44\u6E90\u83B7\u53D6\u5373\u521D\u59CB\u5316</p><p>RAII\uFF0C\u5168\u79F0\u8D44\u6E90\u83B7\u53D6\u5373\u521D\u59CB\u5316\uFF08\u82F1\u8BED\uFF1AResource Acquisition Is Initialization\uFF09\uFF0C\u5B83\u662F\u5728\u4E00\u4E9B\u9762\u5411\u5BF9\u8C61\u8BED\u8A00\u4E2D\u7684\u4E00\u79CD\u60EF\u7528\u6CD5\uFF08\u82F1\u8BED\uFF1AProgramming idiom\uFF09\u3002RAII \u6E90\u4E8E C++\uFF0C\u5728 Java\uFF0CC#\uFF0CD\uFF0CAda\uFF0CVala \u548C Rust \u4E2D\u4E5F\u6709\u5E94\u7528\u3002</p><p>RAII \u8981\u6C42\uFF0C\u8D44\u6E90\u7684\u6709\u6548\u671F\u4E0E\u6301\u6709\u8D44\u6E90\u7684\u5BF9\u8C61\u7684\u751F\u547D\u671F\uFF08\u82F1\u8BED\uFF1AObject lifetime\uFF09\u4E25\u683C\u7ED1\u5B9A\uFF0C\u5373\u7531\u5BF9\u8C61\u7684\u6784\u9020\u51FD\u6570\u5B8C\u6210\u8D44\u6E90\u7684\u5206\u914D\uFF08\u82F1\u8BED\uFF1AResource allocation (computer)\uFF09\uFF08\u83B7\u53D6\uFF09\uFF0C\u540C\u65F6\u7531\u6790\u6784\u51FD\u6570\u5B8C\u6210\u8D44\u6E90\u7684\u91CA\u653E\u3002\u5728\u8FD9\u79CD\u8981\u6C42\u4E0B\uFF0C\u53EA\u8981\u5BF9\u8C61\u80FD\u6B63\u786E\u5730\u6790\u6784\uFF0C\u5C31\u4E0D\u4F1A\u51FA\u73B0\u8D44\u6E90\u6CC4\u9732\uFF08\u82F1\u8BED\uFF1AResource leak\uFF09\u95EE\u9898\u3002</p></div><p>\u53C2\u8003\uFF1A</p><ul><li><a href="https://www.wikiwand.com/en/Resource_acquisition_is_initialization" target="_blank" rel="noreferrer">Resource acquisition is initialization - Wikiwand</a></li><li><a href="https://www.wikiwand.com/zh-hans/RAII" target="_blank" rel="noreferrer">RAII - Wikiwand</a></li></ul><h3 id="\u81EA\u52A8\u5F15\u7528\u8BA1\u6570-arc" tabindex="-1">\u81EA\u52A8\u5F15\u7528\u8BA1\u6570 (ARC) <a class="header-anchor" href="#\u81EA\u52A8\u5F15\u7528\u8BA1\u6570-arc" aria-hidden="true">#</a></h3><p>ARC\uFF08Automatic Reference Counting\uFF09\u4E0E\u5F15\u7528\u8BA1\u6570 GC \u7C7B\u4F3C\uFF0C\u4F46\u4E0D\u662F\u7528\u4E00\u4E2A\u8FD0\u884C\u65F6\u8FDB\u7A0B\uFF08runtime process\uFF09\u8FD0\u884C\u5728\u7279\u5B9A\u7684\u65F6\u95F4\u95F4\u9694\uFF0C\u800C\u662F\u5C06 retain \u548C release \u6307\u4EE4\u5728\u7F16\u8BD1\u671F\u63D2\u5165\u5230\u7F16\u8BD1\u597D\u7684\u4EE3\u7801\u4E2D\uFF0C\u5F53\u4E00\u4E2A\u5BF9\u8C61\u7684\u5F15\u7528\u53D8\u4E3A 0 \u65F6\uFF0C\u5185\u5B58\u4F1A\u88AB\u81EA\u52A8\u6E05\u7406\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u4F1A\u88AB\u4F5C\u4E3A\u7A0B\u5E8F\u6267\u884C\u7684\u4E00\u90E8\u5206\uFF0C\u65E0\u9700\u6682\u505C\u4EFB\u4F55\u7A0B\u5E8F\u3002\u8FD9\u79CD\u65B9\u5F0F\u4E5F\u4E0D\u80FD\u5904\u7406\u5FAA\u73AF\u5F15\u7528\u95EE\u9898\uFF08cyclic references\uFF09\uFF0C\u9700\u8981\u4F9D\u8D56\u4E8E\u5F00\u53D1\u4EBA\u5458\u4F7F\u7528\u7279\u5B9A\u7684\u5173\u952E\u5B57\u53BB\u5904\u7406\u3002\u8FD9\u662F\u4E00\u79CD Clang \u7F16\u8BD1\u5668\u7684\u7279\u6027\uFF0CClang \u4E3A<strong> Objective C</strong> &amp; <strong>Swift</strong> \u63D0\u4F9B\u4E86 ARC \u652F\u6301\u3002</p><h3 id="\u6240\u6709\u6743\uFF08ownership\uFF09" tabindex="-1">\u6240\u6709\u6743\uFF08Ownership\uFF09 <a class="header-anchor" href="#\u6240\u6709\u6743\uFF08ownership\uFF09" aria-hidden="true">#</a></h3><p><strong>Ownership</strong> \u7528\u6240\u6709\u6743\u6A21\u578B\uFF08ownership model\uFF09\u7ED3\u5408\u4E86 RAII\uFF0C\u4EFB\u4F55\u503C\u5FC5\u987B\u6709\u4E00\u4E2A\u53D8\u91CF\u4F5C\u4E3A\u5B83\u7684\u6240\u6709\u8005\uFF08owner\uFF09(\u4E00\u4E2A\u65F6\u95F4\u70B9\u4EC5\u6709\u4E00\u4E2A\u6240\u6709\u8005)\u3002\u5F53\u6240\u6709\u8005\u8D85\u51FA\u4E86\u4F5C\u7528\u57DF\uFF08scope\uFF09\uFF0C\u503C\u5C31\u5C06\u88AB\u4E22\u5F03\uFF0C\u91CA\u653E\u6389\u5185\u5B58\uFF0C\u4E0D\u7BA1\u662F\u5728\u6808\u8FD8\u662F\u5806\u5185\u5B58\u4E2D\u3002\u5B83\u6709\u70B9\u50CF\u7F16\u8BD1\u65F6\u5F15\u7528\u8BA1\u6570\uFF08Compile-time reference counting\uFF09\uFF0C\u6B63\u5728\u88AB Rust \u4F7F\u7528\u3002\u5728\u6211\u7684\u7814\u7A76\u4E2D\uFF0C\u6211\u8FD8\u6CA1\u6709\u53D1\u73B0\u4EFB\u4F55\u5176\u4ED6\u6B63\u5728\u8FD9\u79CD\u8FD9\u79CD\u673A\u5236\u7684\u7F16\u7A0B\u8BED\u8A00\u3002</p><p>\u6211\u4EEC\u521A\u521A\u5BF9\u5185\u5B58\u7BA1\u7406\u6709\u4E86\u7C97\u6D45\u8BA4\u8BC6\u3002\u6BCF\u79CD\u7F16\u7A0B\u8BED\u8A00\u5728\u4F7F\u7528\u5B83\u81EA\u5DF1\u7248\u672C\u7684\u5185\u5B58\u7BA1\u7406\u65B9\u5F0F\uFF0C\u5E76\u5229\u7528\u4E0D\u540C\u7684\u8C03\u6559\u597D\u7684\u7B97\u6CD5\u8FBE\u6210\u4E0D\u540C\u7684\u76EE\u6807\u3002\u5728\u8BE5\u7CFB\u5217\u6587\u7AE0\u63A5\u4E0B\u6765\u7684\u90E8\u5206\u91CC\uFF0C\u6211\u4EEC\u5C06\u6DF1\u5165\u4E86\u89E3\u4E00\u4E9B\u6D41\u884C\u7F16\u7A0B\u8BED\u8A00\u7684\u5177\u4F53\u7684\u5185\u5B58\u7BA1\u7406\u5B9E\u73B0\u65B9\u6848\u3002</p><p><img data-zooming src="https://cdn.staticaly.com/gh/jonsam-ng/image-hosting@master/2022/image.1qmxuzu1625c.webp" alt="image" data-src="https://cdn.staticaly.com/gh/jonsam-ng/image-hosting@master/2022/image.1qmxuzu1625c.webp"></p><h2 id="\u53C2\u8003" tabindex="-1">\u53C2\u8003 <a class="header-anchor" href="#\u53C2\u8003" aria-hidden="true">#</a></h2><ul><li><a href="https://deepu.tech/memory-management-in-programming/" target="_blank" rel="noreferrer">\u{1F680} Demystifying memory management in modern programming languages | Technorage</a></li></ul><div class="warning custom-block"><p class="custom-block-title">\u7248\u6743\u58F0\u660E</p><p>\u672C\u6587\u7FFB\u8BD1\u81EA<a href="https://deepu.tech/memory-management-in-programming/" target="_blank" rel="noreferrer">\u{1F680} Demystifying memory management in modern programming languages</a>\uFF0C\u7248\u6743\u5F52\u539F\u4F5C\u8005\u6240\u6709\u3002</p></div>',28);function g(d,h,p,m,u,f){const a=o("Bilibili");return n(),t("div",null,[l,i(a,{id:"BV1te4y1x7Au"}),c])}const C=e(s,[["render",g]]);export{b as __pageData,C as default};
